    ---
    async checkSDKAvailability() {
      if (!this.mcpManager || !this.mcpManager.servers) {
        throw new Error('MCP servers not available');
      }

      const availableSDKs = [];
      for (const server of this.mcpManager.servers) {
        try {
          const sdk = this.sdkManager.getSDK(server);
          if (sdk) {
            availableSDKs.push(server);
          }
        } catch (error) {
          this.logger.error(`Error checking SDK availability for server ${server}: ${error.message}`);
        }
      }
      return availableSDKs;
    }
