constructor() {
    this.logger = new Logger('EternityModule');
    this.codestralAPI = new CodestralAPI();
    this.memoryManager = new MemoryManager();
    try {
        this.mcpManager = new MCPManager();
    } catch (error) {
        this.logger.warn('Failed to initialize MCPManager', error);
        this.mcpManager = null;
    }
    try {
        this.selfImprovementEngine = new SelfImprovementEngine();
    } catch (error) {
        this.logger.warn('Failed to initialize SelfImprovementEngine', error);
        this.selfImprovementEngine = null;
    }
    this._multiModelOrchestrator = new MultiModelOrchestrator();
    this._autonomousMode = true;
    this._maxAutonomousImprovementsPerCycle = 5;
    this._analysisInterval = setInterval(() => this._analyzeCode(), 60000);
    this._eventEmitter = new EventEmitter();
}
