async initialize() {
    try {
      this.mcpManager = this.container.resolve('mcpManager');
      if (!this.mcpManager) {
        throw new Error('Failed to resolve mcpManager');
      }

      const DEFAULT_CHECK_INTERVAL = 300000; // 5 minutes
      const DEFAULT_MAX_AUTO_FIXES = 5;

      this.config = {
        enabled: process.env.AUTO_CORRECTION_ENABLED === 'true',
        checkInterval: parseInt(process.env.AUTO_CORRECTION_INTERVAL || DEFAULT_CHECK_INTERVAL.toString()),
        maxAutoFixes: parseInt(process.env.AUTO_CORRECTION_MAX_FIXES || DEFAULT_MAX_AUTO_FIXES.toString()),
        allowedSeverity: ['low', 'minor', 'info', 'warning'],
        notificationMode: process.env.AUTO_CORRECTION_NOTIFY || 'subtle',
        pythonSdkEnabled: process.env.MCP_PYTHON_SDK_ENABLED === 'true',
        javaSdkEnabled: process.env.MCP_JAVA_SDK_ENABLED === 'true'
      };

      if (isNaN(this.config.checkInterval) || this.config.checkInterval <= 0) {
        this.logger.error('[AUTO-CORRECTION] Invalid check interval, using default');
        this.config.checkInterval = DEFAULT_CHECK_INTERVAL;
      }

      this.logger.info('[AUTO-CORRECTION] Initialized with config:', {
        enabled: this.config.enabled,
        checkInterval: this.config.checkInterval,
        maxAutoFixes: this.config.maxAutoFixes,
        allowedSeverity: this.config.allowedSeverity,
        notificationMode: this.config.notificationMode,
        pythonSdkEnabled: this.config.pythonSdkEnabled,
        javaSdkEnabled: this.config.javaSdkEnabled
      });

      this.correctionHistory = [];
      this.autoFixCount = 0;
      this.isEnabled = this.config.enabled;
      this.enabled = this.config.enabled;
      this.checkInterval = this.config.checkInterval;
      this.maxAutoFixes = this.config.maxAutoFixes;
      this.allowedSeverity = this.config.allowedSeverity;
